import React, { useState, useEffect } from "react";
import CSVToTable from "react-csv-to-table";
import csvData from "./bestPitches_ex.csv";

export const Results = () => {
  const [playerData, setPlayerData] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [foundPlayer, setFoundPlayer] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const response = await fetch(csvData);
      const data = await response.text();
      let parsedData = data.split("\n").map((row) => row.split(","));
      // Remove any empty rows at the end
      while (parsedData.length > 0 && parsedData[parsedData.length - 1].every((cell) => !cell)) {
        parsedData.pop();
      }
      const headers = parsedData.shift();
      const players = parsedData.map((row) =>
        headers.reduce((obj, header, index) => {
          obj[header] = row[index];
          return obj;
        }, {})
      );
      setPlayerData(players);
    };
    fetchData();
  }, []);

  useEffect(() => {
    const player = playerData.find(
      (data) => data.player_name.toLowerCase() === searchTerm.toLowerCase()
    );
    setFoundPlayer(player);
  }, [searchTerm, playerData]);

  return (
    <div>
      <input
        type="text"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <CSVToTable data={playerData} tableClassName="table table-striped table-hover" />
      {foundPlayer ? (
        <p>{foundPlayer.stuffGrade}</p>
      ) : (
        <p>Player not found</p>
      )}
    </div>
  );
};
